--  DDL for View PRODUCTCONTAINER
--------------------------------------------------------

  CREATE OR REPLACE FORCE VIEW "PRODUCTCONTAINER" ("PRODUCT_DISPLAY_SRC", "BRAND_NAME", "BRAND_ID", "PRODUCT_DISPLAY_NAME", "MINPRICE", "CATEGORY_LARGE_ID", "CATEGORY_MID_ID", "CATEGORY_SMALL_ID", "AFTERPRICE", "PRODUCT_DISPLAY_ID", "PRODUCT_ID", "COUPONFLAG", "DISCOUNTFLAG", "PRESENTFLAG", "TODAYPICKUPFLAG", "ORDERCNT", "PRODUCT_STOCK", "PRODUCT_DISPLAY_LIKE", "PRODUCT_DISPLAY_REGDATE") AS 
  SELECT 
    t.product_display_src,
    t.brand_name, 
    t.brand_id, 
    t.product_display_name,
    t.minprice,
    t.category_large_id,
    t.category_mid_id,
    t.category_small_id,
    t.afterprice,
    t.product_display_id,
    t.product_id,
    t.couponflag,
    t.discountflag,
    t.presentflag,
    t.todaypickupflag,
    COALESCE(SUM(t.product_cnt), 0) AS ordercnt,
    t.product_stock,
    t.product_display_like,
    t.product_display_regdate
FROM (
    SELECT 
        pdi.product_display_src,
        b.brand_name, 
        b.brand_id, 
        pd.product_display_name,
        MIN(p.product_price) OVER (PARTITION  BY pd.product_display_id ORDER BY product_price) as minprice,
        cl.category_large_id,
        cm.category_mid_id,
        cs.category_small_id,
        NVL(CASE 
                WHEN prc.promotion_coupon_kind = 1 
                    THEN MIN(p.product_price) OVER (PARTITION  BY pd.product_display_id ORDER BY product_price) + prc.promotion_coupon_discount
                WHEN prc.promotion_coupon_kind = 2 
                    THEN MIN(p.product_price) OVER (PARTITION  BY pd.product_display_id ORDER BY product_price) * (1-prc.promotion_coupon_discount)
                ELSE MIN(p.product_price) OVER (PARTITION  BY pd.product_display_id ORDER BY product_price)
            END, 0) +
        NVL(prd.promotion_discount_price,0) as afterprice,
        pd.product_display_id,
        p.product_id,
        CASE 
            WHEN prc.promotion_coupon_start_day <= SYSDATE AND prc.promotion_coupon_end_day >= SYSDATE THEN '1'
            ELSE '0'
        END AS couponflag,
        CASE 
            WHEN prd.promotion_discount_start_day <= SYSDATE AND prd.promotion_discount_end_day >= SYSDATE THEN '1'
            ELSE '0'
        END AS discountflag,
        CASE 
            WHEN prp.promotion_present_start_day <= SYSDATE AND prp.promotion_present_end_day >= SYSDATE THEN '1'
            ELSE '0'
        END AS presentflag,
        CASE 
            WHEN ss.max_store_id IS NOT NULL THEN '1'
            ELSE '0'
        END AS todaypickupflag,
        p.product_stock,
        pd.product_display_like,
        pd.product_display_regdate,
        op.product_cnt
    FROM product_display pd
    JOIN product p ON pd.product_display_id = p.product_display_id
    JOIN category_small cs ON cs.category_small_id = p.category_small_id
    JOIN category_mid cm ON cm.category_mid_id = cs.category_mid_id
    JOIN category_large cl ON cl.category_large_id = cm.category_large_id
    JOIN product_display_img pdi ON pd.product_display_id = pdi.product_display_id
    JOIN brand b ON pd.brand_id = b.brand_id
    LEFT JOIN product_promotion pm ON pd.product_display_id = pm.product_display_id
    LEFT JOIN promotion_coupon prc ON pm.promotion_coupon_id = prc.promotion_coupon_id
    LEFT JOIN promotion_discount prd ON prd.promotion_discount_id = pm.promotion_discount_id
    LEFT JOIN promotion_present prp ON prp.promotion_present_id = pm.promotion_present_id
    LEFT JOIN (
        SELECT product_id, MAX(store_id) AS max_store_id
        FROM store_stock
        GROUP BY product_id
    ) ss ON p.product_id = ss.product_id
    LEFT JOIN ORDER_PRODUCT op ON p.product_id = op.product_id
    WHERE pdi.product_display_img = (
        SELECT MAX(product_display_img)
        FROM product_display_img
        WHERE product_display_id = pd.product_display_id
    )
    and
    p.product_id = (
    SELECT MAX(product_id)
    FROM product
    WHERE product_display_id = pd.product_display_id
    )  
) t
GROUP BY 
    t.product_display_src,
    t.brand_name, 
    t.brand_id, 
    t.product_display_name,
    t.minprice,
    t.category_large_id,
    t.category_mid_id,
